  class CollaborationServer:
      def __init__(self):
          self.clients = {}
          self.projects = {}

      async def register_client(self, websocket, project_id, user_id):
          if project_id not in self.clients:
              self.clients[project_id] = {}
          self.clients[project_id][user_id] = websocket

      async def broadcast_update(self, project_id, update_data, sender_id):
          if project_id in self.clients:
              message = json.dumps({
                  'type': 'update',
                  'sender': sender_id,
                  'data': update_data,
                  'timestamp': datetime.now().isoformat()
              })

              disconnected = []
              for user_id, client in self.clients[project_id].items():
                  if user_id != sender_id:
                      try:
                          await client.send(message)
                      except websockets.exceptions.ConnectionClosed:
                          disconnected.append(user_id)

              for user_id in disconnected:
                  del self.clients[project_id][user_id]
