      def calculate_checksum(self, file_path):
          hasher = hashlib.sha256()
          with open(file_path, 'rb') as f:
              while chunk := f.read(8192):
                  hasher.update(chunk)
          return hasher.hexdigest()

      async def monitor_changes(self, directory):
          observer = Observer()
          handler = FileSystemEventHandler()
          handler.on_modified = lambda event: asyncio.create_task(
              self.handle_file_change(event.src_path)
          )
          observer.schedule(handler, directory, recursive=True)
          observer.start()

      async def handle_file_change(self, file_path):
          new_checksum = self.calculate_checksum(file_path)
          old_checksum = self.file_checksums.get(file_path)

          if new_checksum != old_checksum:
              self.file_checksums[file_path] = new_checksum
              await self.sync_queue.put({
                  'action': 'upload',
                  'file_path': file_path,
                  'timestamp': datetime.now()
              })

      async def sync_worker(self):
          while True:
              sync_task = await self.sync_queue.get()
              try:
                  if sync_task['action'] == 'upload':
                      await self.upload_file(sync_task['file_path'])
                  elif sync_task['action'] == 'download':
                      await self.download_file(sync_task['file_path'])
              except Exception as e:
                  logging.error(f"Sync error: {e}")
                  # Retry logic
                  await asyncio.sleep(5)
                  await self.sync_queue.put(sync_task)
