  class CellCountPlugin(PluginInterface):
      def __init__(self):
          super().__init__()
          self.name = "Cell Counter"
          self.version = "1.0.0"

      def execute(self, image, params=None):
          # ì„¸í¬ ê²€ì¶œ ì•Œê³ ë¦¬ì¦˜
          gray = cv2.cvtColor(image, cv2.COLOR_BGR2GRAY)
          _, binary = cv2.threshold(gray, 127, 255, cv2.THRESH_BINARY)

          contours, _ = cv2.findContours(
              binary, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE
          )

          cells = []
          for contour in contours:
              area = cv2.contourArea(contour)
              if params.get('min_area', 100) < area < params.get('max_area', 1000):
                  cells.append({
                      'contour': contour,
                      'area': area,
                      'center': cv2.moments(contour)
                  })

          return {'count': len(cells), 'cells': cells}
